let cart = [];

const itemContainer = document.getElementById('itemContainer');
const modal = document.getElementById('modal');
const itemTitle = document.getElementById('itemTitle');
const itemImage = document.getElementById('itemImage');
const itemDescription = document.getElementById('itemDescription');
const itemPrice = document.getElementById('itemPrice');
const itemReviews = document.getElementById('itemReviews');
const cartCountSpan = document.getElementById('cartCount');

function loadCart() {
    cart = JSON.parse(localStorage.getItem('cart')) || [];
    updateCartCount();
}

items.forEach(item => {
    const div = document.createElement('div');
    div.className = 'item fade-in'; // Added fade-in animation class
    div.innerHTML = `
        <img src="${item.image}" alt="${item.title}" onclick="openModal('${item.title}')" class="item-image">
        <h3>${item.title}</h3>
        <p>Price: â‚¹${item.price}</p>
        <input type="number" id="itemCount-${item.title}" min="1" value="1" style="width: 50px;">
    `;
    
    const addToCartBtn = document.createElement('button');
    addToCartBtn.textContent = "Add to Cart";
    addToCartBtn.onclick = () => addToCart(item);
    div.appendChild(addToCartBtn);
    
    const buyNowBtn = document.createElement('button');
    buyNowBtn.textContent = "Buy Now";
    buyNowBtn.onclick = () => buyNow(item);
    div.appendChild(buyNowBtn);
    
    itemContainer.appendChild(div);
});

function openModal(title) {
    const item = items.find(i => i.title === title);
    itemTitle.textContent = item.title;
    itemImage.src = item.image;
    itemDescription.textContent = item.description;
    itemPrice.textContent = item.price;
    itemReviews.innerHTML = item.reviews.map(review => `<p>${review}</p>`).join('');
    modal.style.display = "block";
    modal.classList.add('fade-in'); // Add fade-in animation to the modal
}

function closeModal() {
    modal.classList.remove('fade-in');
    modal.style.display = "none";
}

document.querySelector('.close').onclick = closeModal;

function addToCartFromModal() {
    const title = itemTitle.textContent;
    const count = document.getElementById(`itemCount-${title}`).value || 1;
    addToCart({ title, count: parseInt(count) });
    closeModal();
}

function addToCart(item) {
    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    const existingItem = cart.find(i => i.title === item.title);
    if (existingItem) {
        existingItem.count += parseInt(document.getElementById(`itemCount-${item.title}`).value || 1);
    } else {
        cart.push({ title: item.title, count: parseInt(document.getElementById(`itemCount-${item.title}`).value || 1) });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    updateCartCount();
}

function buyNowFromModal() {
    addToCartFromModal();
    window.location.href = "checkout.html";
}

function updateCartCount() {
    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    cartCountSpan.textContent = cart.reduce((total, item) => total + item.count, 0);
}

function openCart() {
    window.location.href = "checkout.html";
}

loadCart();